#!/usr/bin/env python
#coding:utf-8
import threading
from thrift.transport import TSocket, TTransport
from thrift.protocol import TBinaryProtocol
from pyutil.thrift.transport import SocketPool
from pyutil.program.icrawler_util import str_dict
from ss.abtest import VersionService

class ThriftVersionService(threading.local):

    def __init__(self, host, port, timeout=0.1, conn_timeout=0.05):
        transport = SocketPool.TSocketPool(host, port, timeout, conn_timeout)
        transport = TTransport.TBufferedTransport(transport)
        protocol = TBinaryProtocol.TBinaryProtocol(transport)
        self.client = VersionService.Client(protocol)
        self.client.transport = transport

    def get_flight(self):
        try:
            self.client.transport.open()
            rsp = self.client.get_flight()
            return rsp
        finally:
            self.client.transport.close()

    def get_version_with_name(self, req):
        try:
            self.client.transport.open()
            rsp = self.client.get_version_with_name(req)
            return rsp
        finally:
            self.client.transport.close()
    
    def get_version_with_property(self, req):
        try:
            self.client.transport.open()
            rsp = self.client.get_version_with_property(req)
            return rsp
        finally:
            self.client.transport.close()
