#!/usr/bin/env python
#coding:utf-8
import threading
from datetime import datetime
from thrift.transport import TTransport
from thrift.protocol import TBinaryProtocol
from pyutil.thrift.transport import TSocketPool
from pyutil.program.icrawler_util import str_dict
from ss.article.article_politics_classify import ArticlePoliticsClassify
from ss.article.article_politics_classify.ttypes import PoliticsClassifyReq

class ThriftPoliticsClassifier(threading.local):

    def __init__(self, host, port):
        transport = TSocketPool.TSocketPool(host, port)
        transport = TTransport.TBufferedTransport(transport)
        protocol = TBinaryProtocol.TBinaryProtocol(transport)
        self.client = ArticlePoliticsClassify.Client(protocol)
        self.client.transport = transport

    def classify(self, article, extra={}):
        self.unicode2utf8(article)
        str_dict(article)
        req = PoliticsClassifyReq(article, extra)
        try:
            self.client.transport.open()
            rsp = self.client.classify(req)
            return rsp.status, rsp.is_politics, rsp.confidence, rsp.msg
        finally:
            self.client.transport.close()

    def unicode2utf8(self, article):
        for k, v in article.iteritems():
            if isinstance(v, unicode):
                article[k] = v.encode('utf8')
            elif isinstance(v, datetime):
                article[k] = v.strftime('%Y-%m-%d %H:%M:%S')
