#!/usr/bin/env python
#coding:utf-8
import threading
from thrift.transport import TSocket, TTransport
from thrift.protocol import TBinaryProtocol
from pyutil.thrift.transport import HashRingSocketPool
from pyutil.program.icrawler_util import str_dict
from recommend_cython import Recommend
from recommend_cython.ttypes import Req, Rsp

class ThriftRecommender(threading.local):

    def __init__(self, host, port, timeout=1.0, conn_timeout=0.2):
        self.socketpool = HashRingSocketPool.TSocketPool(host, port, timeout, conn_timeout)
        transport = TTransport.TBufferedTransport(self.socketpool)
        protocol = TBinaryProtocol.TBinaryProtocolAccelerated(transport)
        self.client = Recommend.Client(protocol)
        self.client.transport = transport

    def recommend(self, req):
        try:
            key = str(req.users[0])
            self.socketpool.open(key)
            rsp = self.client.get(req)
            return rsp
        finally:
            self.client.transport.close()

    def get_peer_addr(self):
        return "%s:%s" % (self.socketpool.host, self.socketpool.port)
