#!/usr/bin/env python
# coding: utf-8
__author__ = 'zhenghuabin'

from pyutil.program.conf2 import Conf


class Idc(object):
    local_dc = None
    remote_dcs = []

    @classmethod
    def get_local_dc(cls):
        cls._parse_dcs()
        return cls.local_dc

    @classmethod
    def get_remote_dcs(cls):
        cls._parse_dcs()
        return cls.remote_dcs

    @classmethod
    def _parse_dcs(cls):
        if not cls.local_dc:
            conf = Conf('/etc/ss_conf/idc.conf')
            cls.local_dc = conf.get('consul_local_dc')
            cls.remote_dcs = conf.get_values('consul_remote_dc')

