# coding: utf-8
from django.db import models
from django.contrib.auth.models import User, Group

# Create your models here.

TABLE_PREFIX = 'gm_'


class GroupManager(models.Model):
    """
    user: the manager
    groups: the permission groups that the superuser has assigned to the manager
    """

    class Meta:
        db_table = TABLE_PREFIX + 'group_manager'
        verbose_name = u'组管理员'
        verbose_name_plural = u'组管理员'

    user = models.ForeignKey(User, unique=True, null=True, blank=True)
    groups = models.ManyToManyField(Group)

    def __unicode__(self):
        return self.user.username


class GroupAssignRecord(models.Model):
    """
    记录一个group被assigned给了哪些人
    """

    class Meta:
        db_table = TABLE_PREFIX + 'group_assign_record'
        verbose_name = u'权限分配记录'
        verbose_name_plural = u'权限分配记录'

    group = models.ForeignKey(Group, unique=True, related_name='+')
    users = models.ManyToManyField(User)

