from pyutil.i18n.util import is_i18n_env, get_region
from ss_data.domain.const.region import Region

class I18nConfig(object):
    def __init__(self, default_conf, i18n_conf=None, i18n_sg_conf=None):
        '''
        :param dict default_conf:
        :param dict i18n_conf:

        >>> config = I18nConfig(dict(a=1, b=2), dict(a=2))
        >>> config.a
        2
        >>> config.b
        2
        '''
        config = dict(default_conf)
        if is_i18n_env():
            if Region.SG in get_region():
                config.update(i18n_sg_conf)
            else:
                config.update(i18n_conf)
        self.__dict__.update(config)
