import unittest
import sys, os
import subprocess

class Test(unittest.TestCase):

        def testGetElementByXPath(self):
                self.__runImpl(sys._getframe().f_code.co_name)

        def testGetChildrenByTagName(self):
                self.__runImpl(sys._getframe().f_code.co_name)

        def testGetAttributeValue(self):
                self.__runImpl(sys._getframe().f_code.co_name)

        def testGetElementsTexts(self):
                self.__runImpl(sys._getframe().f_code.co_name)

        def testGetInnerHTML(self):
                self.__runImpl(sys._getframe().f_code.co_name)

        def __runImpl(self, param):
                testwd = os.path.split(os.path.realpath(__file__))[0]
                subprocess.Popen("python testimpl.py %s" % param, 
                                 shell=True, 
                                 cwd = testwd).communicate()

if __name__ == '__main__':
        unittest.main()

