# coding: utf-8

"""
Utilities for strings.
"""

import random
import string

CHARSET_ALL = string.letters + string.digits

def random_string(length, charset=CHARSET_ALL):
    """
    Returns a random string of a given length and charset.

    Not a true-secure method.
    """
    return "".join(random.SystemRandom().choice(charset) for _ in xrange(length))


def random_string_numeric(length):
    """
    Returns a random string consisting of numeric characters.
    """
    return random_string(length, string.digits)


def random_string_lowercase(length):
    """
    Returns a random string consisting of lowercase characters.
    """
    return random_string(length, string.lowercase)


def random_string_uppercase(length):
    """
    Returns a random string consisting of uppercase characters.
    """
    return random_string(length, string.uppercase)
