# coding: utf-8

"""
Unit tests for random utilities.
"""

import unittest

from pyutil.random import utils as random_utils

class RandomUtilsTestCase(unittest.TestCase):
    """
    Test cases random utils unittests.
    """

    def test_random_charset(self):
        """
        Unit tests for charsets.
        """
        charset = "qwerty"
        length = 10
        string = random_utils.random_string(length, charset)
        self.assertEqual(length, len(string))
        for i in xrange(length):
            self.assertIn(string[i], charset)
