#coding=utf8

import yaml
from pyutil.program.python import obj_unicode_to_str, unicode_json_dump # 兼容其他程序的调用
from pyutil.program.fmtutil import shorten, pformat # 兼容其他程序的调用

def unicode_yaml_dump(d):
    return yaml.safe_dump(d, allow_unicode=True, default_flow_style=False).decode('utf8')

def pformat_dict(d):
    '''
    return - unicode
    '''
    return yaml.safe_dump(d, allow_unicode=True, default_flow_style=True).strip().decode('utf8')
