#!/usr/bin/env python
#coding:utf-8
import threading
from thrift.transport import TSocket, TTransport
from thrift.protocol import TBinaryProtocol
from pyutil.thrift.transport import SocketPool, SocketPool2
from ss.site.voice import VoiceCommand
from ss.site.voice.ttypes import Req, Rsp

class ThriftVoiceCommand(threading.local):

    def __init__(self, host, port, timeout=0.1, conn_timeout=0.05):
        transport = SocketPool2.TSocketPool(host, port, timeout, conn_timeout)
        transport = TTransport.TBufferedTransport(transport)
        protocol = TBinaryProtocol.TBinaryProtocol(transport)
        self.client = VoiceCommand.Client(protocol)
        self.client.transport = transport

    def command(self, req):
        try:
            self.client.transport.open()
            rsp = self.client.command(req)
            return rsp
        finally:
            self.client.transport.close()
